create table POSZT
(
    ID         NUMBER generated as identity
        primary key,
    CSOPORT_ID NUMBER
        references CSOPORT
            on delete cascade,
    SZOVEG     VARCHAR2(4000),
    LIKES      NUMBER,
    TIME       TIMESTAMP(6),
    FELH_ID    NUMBER
        constraint "POSZT_FELHASZNALO_ID_fk"
            references FELHASZNALO
)
/


